import React, { useState } from "react";
import "../css/ExpenseForm.css";

const ExpenseForm = () => {
  const [TitleEnter, setEnterTitle] = useState("");
  const [TitleAmount, setAmountTitle] = useState("");
  const [TitleDate, setDateTitle] = useState("");

  const titleChangeHanlder = (event) => {
    setEnterTitle(event.target.value);
    setDateTitle(event.target.value);
    setAmountTitle(event.target.value);
    console.log(TitleEnter);
  };

  return (
    <form>
      <div className="new-expense__controls">
        <div className="new-expense__control">
          <label>Title</label>
          <input type="text" onChange={titleChangeHanlder} />
        </div>
        <div className="new-expense__control">
          <label>Amount</label>
          <input type="number" min="0.01" step="0.01" onChange={titleChangeHanlder} />
        </div>
        <div className="new-expense__control">
          <label>Date</label>
          <input type="date" min="2019-01-01" max="2022-12-31" onChange={titleChangeHanlder} />
        </div>
      </div>
      <div className="new-expense__actions">
        <button type="submit">Add Expense</button>
      </div>
    </form>
  );
};

export default ExpenseForm;
