import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "../css/Expenses.css";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const ExpenseChangeFilter = (selectYear) => {
    setFilteredYear(selectYear);
  };

  const filterOnYear = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={ExpenseChangeFilter}
        />
        {filterOnYear.length === 0 && <h4>There is not data</h4>}
        {filterOnYear.length > 0 &&
          filterOnYear.map((expense) => {
            return (
              <ExpenseItem
                key={expense.id}
                title={expense.title}
                amount={expense.amount}
                date={expense.date}
                pay={expense.pay}
              />
            );
          })
        }
      </Card>
    </div>
  );
};

export default Expenses;
