import React, { useState } from "react";
import ExpenseItem from "./ExpenseItem";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpensesFilter";
import "../css/Expenses.css";

const Expenses = (props) => {
  const [filteredYear, setFilteredYear] = useState("2020");
  const ExpenseChangeFilter = (selectYear) => {
    setFilteredYear(selectYear);
  };

  const filterOnYear = props.items.filter((expense) => {
    return expense.date.getFullYear().toString() === filteredYear;
  });

  let expensesContent = <p>There is no data found</p>

  if (filterOnYear.length > 0) {
    expensesContent = filterOnYear.map((expense) => {
      return (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
          pay={expense.pay}
        />
      );
    })
  }

  return (
    <div>
      <Card className="expenses">
        <ExpensesFilter
          selected={filteredYear}
          onChangeFilter={ExpenseChangeFilter}
        />
        {expensesContent}
      </Card>
    </div>
  );
};

export default Expenses;
